import { useState, useCallback, useEffect } from "react";
import useWebSocket, { ReadyState } from "react-use-websocket";

const SOCKET_URL = "ws://localhost:1337";

export const WebSocketDemo = () => {
  const [messages, setMessages] = useState<MessageEvent[]>([]);

  const { lastMessage, readyState, sendMessage } = useWebSocket(SOCKET_URL);

  useEffect(() => {
    if (lastMessage !== null) {
      setMessages((prevState) => [...prevState, lastMessage]);
    }
  }, [lastMessage, setMessages]);

  const handleClickSendMessage = useCallback(
    () => sendMessage("Hello"),
    [sendMessage]
  );

  const connectionStatus = {
    [ReadyState.CONNECTING]: "Connecting",
    [ReadyState.OPEN]: "Open",
    [ReadyState.CLOSING]: "Closing",
    [ReadyState.CLOSED]: "Closed",
    [ReadyState.UNINSTANTIATED]: "Uninstantiated",
  }[readyState];

  return (
    <div>
      <button onClick={handleClickChangeSOCKET_URL} type="button">
        Click Me to change Socket Url
      </button>
      <button
        disabled={readyState !== ReadyState.OPEN}
        onClick={handleClickSendMessage}
        type="button"
      >
        Click Me to send Hello
      </button>
      <span>The WebSocket is currently {connectionStatus}</span>
      {lastMessage ? <span>Last message: {lastMessage.data}</span> : null}
      <ul>
        {messages.map((message) => (
          <span key={`${message.data}.${message.timeStamp}`}>
            {message ? message.data : null}
          </span>
        ))}
      </ul>
    </div>
  );
};
